/* 바코드 발행 시 외경 정보를 가져 온다. */ SELECT OD_NAME, OD_Value,
Prod_Code FROM T_OutDiameter where OD_Value <> 0
order by OD_Name

/* 스캘프 투입 현황 검색 투입 현황을 리스트화 하기 위해 처음 3번째 까지는 중복된 데이터로 두고, 이 들에 대한 항목명을 한글로 해 둠. */ select me.Mat_LOT_NO as 코드 ,me.Usable_WGT as 중량 ,REPLACE( ( SELECT DISTINCT SYS_NAME FROM T_CodeMaster WHERE Sys_Code = Mat_Quality AND Sys = 'MM' and Code_Type = '48' ) ,'STS','') as 강종 ,me.Mat_LOT_NO ,me.THK ,me.Usable_WGT ,me.OD_LHT ,REPLACE( ( SELECT DISTINCT SYS_NAME FROM T_CodeMaster WHERE Sys_Code = Mat_Quality AND Sys = 'MM' and Code_Type = '48' ) ,'STS','') as Qual ,CONVERT(VARCHAR, me.Enter_Date, 102) as Enter_Date ,me.MAT_STATUS ,me.OD_VALUE ,me.MAT_BARCODE ,me.Mat_Quality ,me.Mat_Code ,mu.MakeDate from T_Mat_Enter me left outer join T_Mat_Use mu on me.Mat_Barcode = mu.Mat_Barcode where mu.PO_No = @p_PO_No
order by mu.MakeDate
/*같은 자재를 중복 투입할 경우 에러남 -_-*/ /* from T_Plan_Mat_Use mu left outer join T_Mat_Enter me on mu.Mat_Barcode = me.Mat_Barcode where mu.PO_No = @p_PO_No order by me.Enter_Date desc*/

/* 제품 투입 시 사용 */ update T_Prod_Enter set Usable_Qty = 0 /*투입 시 가용 잔량을 0으로 만들 것 - 요청(조부연[과장] - 121116)*/ ,Prod_Status = 'U' where Prod_Barcode = @p_Barcode ;
update T_Plan_Runing set Status = 'I' where PO_No = @p_PO_No ;
update T_Plan_Detail set Status = 'I' where PO_No = @p_PO_No ;
update T_Plan_Header set Status = 'I' where PO_No = @p_PO_No ;

/* 스켈프 투입 시 사용 */ update T_Mat_Enter set /*Usable_WGT = 0*/ Mat_Status = 'U' where Mat_Barcode = @p_Mat_Barcode ;
update T_Plan_Runing set Status = 'I' where PO_No = @p_PO_No ;
update T_Plan_Detail set Status = 'I' where PO_No = @p_PO_No ;
update T_Plan_Header set Status = 'I' where PO_No = @p_PO_No ;

/* 공정 현황 등록 화면에서 실적수량 추가 */ /* 조건:: 1. 인수인 공정 코드의 다음 공정에 해당되는 코드를 구한다. 2. 그 코드 값이 '' 이라면 현재 공정이 끝이므로 상품 수량 이월을 하지 않는다. 3. 현재 공정이 1B인데 다음이 1C일 경우 그 다음 공정을 가리켜야 한다. */
/* :::Test용 변수 선언부. 실제 쿼리 수행 시 파라메터로 사용 한다. declare @p_NextProcCode varchar(2) ,@p_Proc_Code varchar(2) ,@p_PO_No varchar(20) ,@p_Prod_Qty_Add int ,@p_Eq_Code varchar(5) ,@p_NextProcCode varchar (2)
set @p_PO_No = '10A26003' set @p_Proc_Code = '1A' */
UPDATE T_Plan_Runing SET Prod_Qty = Prod_Qty + @p_Prod_Qty_Add WHERE PO_No = @p_PO_No and Proc_Code = @p_Proc_Code ;
/* 코일/제품투입 시 받은 수량 업뎃 */ if ((select count(*) from T_Plan_Runing_Recv where PO_No = @p_PO_No and Proc_Code = @p_Proc_Code) = 0) begin insert into T_Plan_Runing_Recv ( PO_No, Proc_Code, Recv_Qty ) values ( @p_PO_No, @p_Proc_Code, 0 - @p_Prod_Qty_Add ) end else begin UPDATE T_Plan_Runing_Recv SET Recv_Qty = Recv_Qty - @p_Prod_Qty_Add WHERE PO_No = @p_PO_No and Proc_Code = @p_Proc_Code end ;
/* 다음 공정에서 받은 수량 데이터가 존재하지 않으면 새로 삽입 한다. */ if ( @p_NextProcCode <> '' ) begin if ((select count(*) from T_Plan_Runing_Recv where PO_No = @p_PO_No and Proc_Code = @p_NextProcCode) = 0) begin insert into T_Plan_Runing_Recv ( PO_No, Proc_Code, Recv_Qty ) values ( @p_PO_No, @p_NextProcCode, @p_Prod_Qty_Add ) end else begin UPDATE T_Plan_Runing_Recv SET Recv_Qty = Recv_Qty + @p_Prod_Qty_Add WHERE PO_No = @p_PO_No and Proc_Code = @p_NextProcCode end end ;


/* 제품(파이프) 잔량 등록 쿼리 */
declare
	@iRowCount int

set
	@iRowCount = (select Count(*) from T_Plan_Prod_Use where PO_No = @p_PO_No)

insert into T_Plan_Prod_Use
(
	PO_No,
	PO_Seq,
	Proc_Code,
	Use_Prod_No, /* 현재 제품에 대한 사용 횟수 */
	Prod_Barcode,
	Use_Qty,
	Maker,
	MakeDate,
	Editor,
	EditDate
)
values
(
	@p_PO_No,
	@p_PO_Seq,
	@p_Proc_Code,
	@iRowCount + 1,
	@p_Prod_Barcode,
	@p_Use_Qty,
	@p_Maker,
	@p_MakeDate,
	@p_Editor,
	@p_EditDate
)

