SELECT /*PH.Busi_Place ,PH.Work_Area ,PH.PO_No ,PD.PO_Seq ,PH.PO_Date ,PH.Order_Date ,PH.Due_Comp_Date ,(Select Code_Nm From SYS_CodeMaster Where Major_Cd = 'Common[Status]' And Minor_Cd = PH.Status) AS Status ,PH.Cust_Code ,(SELECT [Cust_Name_SN] FROM T_Customer WHERE [Cust_Code] = PH.Real_Cust_Code) AS Real_Cust_Name ,PH.Real_Cust_Code ,PD.Prog_Div ,PD.PO_Div ,PD.Ex_Div ,PD.ERP_PO_No ,PD.ERP_PO_Seq ,PD.ERP_PO_Date ,PD.Dept_Code ,PD.Emp_Code ,PH.Quality ,PD.Comp_Prod ,PD.Comp_Prod_Code ,PD.Comp_OD_Name ,PD.Comp_OD_Value ,PD.Comp_THK ,PD.Comp_LEN ,PD.Comp_Qty ,PD.Comp_WGT ,PD.P1_Prod_Code ,PD.P1_OD_Name ,PD.P1_OD_Value ,PD.P1_THK ,PD.P1_LEN ,PD.P1_Qty ,PD.P1_WGT ,PD.P2_Prod_Code ,PD.P2_OD_Name ,PD.P2_OD_Value ,PD.P2_THK ,PD.P2_LEN ,PD.P2_Qty ,PD.P2_WGT ,PD.Proc_P1_1A ,PD.Proc_P1_1B ,PD.Proc_P1_1C ,PD.Proc_P1_1D ,PD.Proc_P1_1E ,PD.Proc_P2_2A ,PD.Proc_P2_2B ,PD.Proc_P2_2C ,PD.Proc_P2_2D ,PD.Proc_P2_2E ,PD.Must_Proc_Code ,PD.Must_Proc_Name ,PH.Eq_Code ,PD.Remark ,PD.Proc_DB ,PD.ERP_LEN ,PD.ERP_Qty ,PD.POP_LEN ,PD.POP_Qty ,PD.DB_Value */ M.Eq_Code ,D.PO_No ,D.PO_Seq ,CONVERT(NVARCHAR,M.ORDER_DATE,111) AS ORDER_DATE ,CONVERT(NVARCHAR,D.ERP_PO_Date,111) AS ERP_PO_Date ,D.Prog_Div ,(CASE WHEN D.PO_Div = 'O' THEN '주문' ELSE '재고' END) AS PO_Div ,D.PO_Div As PO_Div_Code ,D.Ex_Div ,(SELECT [Cust_Name_SN] FROM T_Customer WHERE [Cust_Code] = M.Real_Cust_Code) AS Real_Cust_Name ,M.Real_Cust_Code ,D.Comp_Prod_Code ,(SELECT PROD_NAME FROM T_PRODUCT WHERE Prod_Code = D.[Comp_Prod_Code]) AS Comp_Prod_Name ,D.Comp_OD_Value ,D.Comp_OD_Name ,D.Comp_THK ,D.Remark ,D.ERP_LEN as COMP_LEN ,D.ERP_Qty as COMP_Qty /* 주문길이,수량 변경 ,D.Comp_LEN ,D.Comp_Qty */ ,D.Comp_WGT ,(SELECT PROD_NAME FROM T_PRODUCT WHERE Prod_Code = D.[P1_Prod_Code]) AS P1_Prod_Name ,(SELECT PROD_NAME FROM T_PRODUCT WHERE Prod_Code = D.[P2_Prod_Code]) AS P2_Prod_Name ,D.[P1_Prod_Code] ,D.[P1_OD_Name] ,D.[P1_OD_Value] ,D.[P1_THK] ,D.[P1_LEN] ,ROUND(CONVERT(DECIMAL(18,0), D.P1_WGT),2) AS P1_WGT ,D.P1_Qty ,D.[P2_Prod_Code] ,D.[P2_OD_Name] ,D.[P2_OD_Value] ,D.[P2_THK] ,D.[P2_LEN] ,ROUND(CONVERT(DECIMAL(18,0), D.P2_WGT),2) AS P2_WGT ,D.P2_Qty ,SUBSTRING( CONVERT(NVARCHAR,M.Due_Comp_Date,111), 6, 5) AS Due_Comp_Date ,D.Must_Proc_Code ,M.Status ,(CASE WHEN M.Status = 'A' THEN '지시대기' WHEN M.Status = 'I' THEN '생산중' WHEN M.Status = 'W' THEN '생산중지' WHEN M.Status = 'X' THEN '지시취소' WHEN M.Status = 'C' THEN '주문취소' ELSE '생산완료' END) AS Status_Kor ,D.Must_Proc_Name ,D.ERP_Len ,(SELECT Quality FROM T_Product WHERE Prod_Code = D.P1_Prod_Code) AS Quality1 ,(SELECT Quality FROM T_Product WHERE Prod_Code = D.P2_Prod_Code) AS Quality2 ,REPLACE((SELECT Sys_Name FROM T_CodeMaster WHERE SYS_CODE =(SELECT Quality FROM T_Product WHERE Prod_Code = Comp_Prod_Code)),'STS','') AS Qual ,M.Quality as Comp_Quality ,ISNULL(p1_len, ISNULL(p2_len, 0)) as OrderLength ,ISNULL(p1_qty, ISNULL(p2_qty, 0)) as OrderQuantity ,PHP.Published ,(CASE WHEN PHP.Published = 'Y' THEN '발행' ELSE '미발행' END) AS Published_Kor ,PHP.Seq ,CONVERT(NVARCHAR,M.PO_Date,111) AS PO_Date FROM T_Plan_Header M LEFT OUTER JOIN T_Plan_Detail D ON M.PO_No = D.PO_No LEFT OUTER JOIN T_Customer C ON M.Cust_Code = C.Cust_Code LEFT OUTER JOIN T_Plan_Header_Published PHP ON M.PO_No = PHP.PO_No WHERE 1 = 1 AND ( M.PO_No like '%' + @Arg_PO_NO + '%') AND ( M.PO_Date Between @Arg_PO_Date_Fr And @Arg_PO_Date_To ) AND ( M.Order_Date Between @Arg_Order_Date_Fr And @Arg_Order_Date_To) AND (( M.Due_Comp_Date Between @Arg_Due_Date_Fr And @Arg_Due_Date_To ) ) /*AND ( M.Cust_Code like '%' + @Arg_Cust_Code + '%' )*/ AND ( ( C.Cust_Name like '%' + @Arg_Cust_Name + '%' ) or ( isnull(C.Cust_Name, '') = '') ) AND ( D.Comp_Prod_Code like '%' + @Arg_Prod_Code + '%') AND ( M.Eq_Code like '%' + @Arg_Eq_Code + '%' ) /*--AND ( D.Comp_OD_Value = @Arg_OD_Value )*/ AND ( D.Comp_OD_Value Between cast(@Arg_OD_Value_Fr as decimal(18,1)) AND cast(@Arg_OD_Value_To as decimal(18,1)) ) AND ( D.Comp_THK Between cast(@Arg_THK_Fr as decimal(18,0)) AND cast(@Arg_THK_To as decimal(18,0)) ) AND ( D.ERP_LEN Between cast(@Arg_LEN_Fr as decimal(18,0)) AND cast(@Arg_LEN_To as decimal(18,0)) ) /*AND ( D.COMP_LEN Between cast(@Arg_LEN_Fr as decimal(18,0)) AND cast(@Arg_LEN_To as decimal(18,0)) ) 주문길이 변경*/ /*AND ( D.Comp_Prod_Code IN ( Select Prod_Code From T_Product Where Quality like '%' + @Arg_Quality + '%' ) )*/ AND ( D.Comp_Prod_Code like '%' + @Arg_Quality + '%' ) AND ( ((@Arg_Published = 'Y') and (PHP.Published = 'Y')) or ((@Arg_Published = 'N') and (PHP.Published is null )) or ((@Arg_Published = 'A') and (PHP.Published = 'Y' or PHP.Published is null)) )
ORDER BY M.PO_No, D.PO_Seq

/* */ /* declare @p_Enter_Date_Fr varchar(12) ,@p_Enter_Date_To varchar(12) ,@p_Prod_Code varchar(12) ,@p_OD_Name varchar(12) ,@p_THK varchar(12) ,@p_LEN varchar(12) ,@p_Qty varchar(12)
select @p_Enter_Date_Fr = '2012-06-01' ,@p_Enter_Date_To = '2012-12-01' ,@p_Prod_Code = '' ,@p_OD_Name = '' ,@p_THK = '1' ,@p_LEN = '6' ,@p_Qty = '1' */
select pe.Prod_Barcode ,pe.Cust_Code ,(SELECT Cust_Name_SN FROM T_CUSTOMER WHERE CUST_CODE = pe.Cust_Code) AS Cust_Name ,pe.Prod_Code ,(SELECT Prod_Name FROM T_Product WHERE Prod_Code = pe.Prod_Code) as Prod_Name ,pe.OD_Name ,pe.OD_Value ,pe.THK ,pe.LEN ,cc.Total_Qty /*,(SELECT SUM(Usable_Qty) FROM T_Prod_Enter where SUBSTRING(Prod_Barcode,1,17) = SUBSTRING(pe.Prod_Barcode,1,17) GROUP BY SUBSTRING(Prod_Barcode,1,17)) AS Total_Qty*/ ,pe.Usable_Qty from T_Prod_Enter pe
left outer join (SELECT SUM(Usable_Qty) AS Total_Qty,SUBSTRING(Prod_Barcode,0,17) AS Common_Code FROM T_Prod_Enter GROUP BY SUBSTRING(Prod_Barcode,0,17)) cc on SUBSTRING(pe.Prod_Barcode,0,17) = cc.Common_Code
where Enter_Date between @p_Enter_Date_Fr and dateadd(dd, 1, @p_Enter_Date_To) and Prod_Code like '%' + @p_Prod_Code + '%' and /*SPEC*/ OD_Name like '%' + @p_OD_Name + '%' and /*호칭경*/ THK like '%' + @p_THK + '%' and /*두께*/ LEN like '%' + @p_LEN + '%' and /*길이*/ ( ((@p_Qty = '') and (Usable_Qty >= 0)) or ((@p_Qty = '0') and (Usable_Qty = 0)) or ((@p_Qty = '1') and (Usable_Qty >= 1)) )
order by pe.Prod_Barcode asc;
